import React from 'react';
import { Box, Button, TextInput, Heading } from "grommet";
import { FormAdd } from "grommet-icons";

export class AddExperiment extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            dueDate: (new Date()).toISOString(),
            process: '',
            steps: '',
        };

        this.changeName.bind(this);
        this.addElement.bind(this);
    }

    changeName(e) {
        this.setState({
            name: e.target.value,
        });
    }

    changeDate(e) {
        this.setState({
            dueDate: e.target.value,
        });
    }

    changeProcess(e) {
        this.setState({
            process: e.target.value,
        });
    }

    changeSteps(e) {
        this.setState({
            steps: e.target.value,
        });
    }

    addElement() {
        this.props.db.put({
            _id: new Date().toJSON(),
            done: false,
            name: this.state.name,
            process: this.state.process,
            dueDate: this.state.dueDate.toJSON(),
            steps: this.state.steps,
        });
    }

    render() {
        return (
            <Box>
                <Box pad="medium" border={{ side: 'top', color: 'light-3' }}>
                    <Heading level="3" margin="none">
                        Add a new experiment
                    </Heading>
                </Box>
                <Box direction="row" pad="medium" gap="small">
                    <TextInput onChange={this.changeName.bind(this)} placeholder="Enter the name of your experiment" value={this.state.name}/>
                    <TextInput onChange={this.changeProcess.bind(this)} placeholder="Enter the process of your experiment" value={this.state.process}/>
                    <TextInput type="date" onChange={this.changeDate.bind(this)} placeholder="Enter the process of your experiment" value={this.state.dueDate}/>
                    <TextInput onChange={this.changeSteps.bind(this)} placeholder="Enter the steps of your experiment" value={this.state.steps}/>
                    <Button icon={<FormAdd />} onClick={this.addElement.bind(this)} primary type="button" />
                </Box>
            </Box>
        );
    }
}

export default AddExperiment;
